   rasterImageViewer1.InteractiveMode = RasterViewerInteractiveMode.Region;
   rasterImageViewer1.InteractiveRegionType = RasterViewerInteractiveRegionType.Rectangle;
   RasterCodecs codecs = new RasterCodecs();
